import React, { useState, useEffect } from 'react';

const Contact = () => {
  const [formData, setFormData] = useState({ 
    name: '', 
    email: '', 
    message: '' 
  });
  
  const [currentImageIndex, setCurrentImageIndex] = useState(0);
  
  // Sample background images - replace with your actual images
  const backgroundImages = [
    'https://images.unsplash.com/photo-1586023492125-27b2c045efd7?w=1920&h=1080&fit=crop',
    'https://images.unsplash.com/photo-1555041469-a586c61ea9bc?w=1920&h=1080&fit=crop',
    'https://images.unsplash.com/photo-1616486338812-3dadae4b4ace?w=1920&h=1080&fit=crop',
    'https://images.unsplash.com/photo-1522771739844-6a9f6d5f14af?w=1920&h=1080&fit=crop'
  ];

  // Change background image every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentImageIndex((prev) => (prev + 1) % backgroundImages.length);
    }, 5000);
    return () => clearInterval(interval);
  }, [backgroundImages.length]);

  // Get the API URL based on environment
  const getApiUrl = () => {
    // If running in development and accessing from a different device
    // Replace 'YOUR_LAPTOP_IP' with your actual laptop's IP address
    const isDevelopment = process.env.NODE_ENV === 'development' || window.location.hostname === 'localhost';
    
    if (isDevelopment) {
      // For mobile access, use your laptop's IP address instead of localhost
      // You can find your IP by running 'ipconfig' (Windows) or 'ifconfig' (Mac/Linux)
      const hostname = window.location.hostname;
      
      if (hostname === 'localhost' || hostname === '127.0.0.1') {
        // Running on the same device
        return 'http://localhost:5000/api/contact';
      } else {
        // Running on a different device (mobile), use the current hostname with backend port
        return `http://${hostname}:5000/api/contact`;
      }
    }
    
    // For production, use relative URL or your production API URL
    return '/api/contact';
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const apiUrl = getApiUrl();
      console.log('Attempting to send to:', apiUrl); // For debugging
      
      const response = await fetch(apiUrl, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          // Add CORS headers if needed
          'Accept': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      
      const result = await response.json();
      alert(result.message || 'Message sent successfully!');
      
      // Clear form on success
      setFormData({ name: '', email: '', message: '' });
    } catch (error) {
      console.error('Contact form error:', error);
      alert(`Error sending message: ${error.message}`);
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Dynamic Background Images */}
      <div className="absolute inset-0 z-0">
        {backgroundImages.map((image, index) => (
          <div
            key={index}
            className={`absolute inset-0 bg-cover bg-center transition-opacity duration-1000 ${
              index === currentImageIndex ? 'opacity-70' : 'opacity-0'
            }`}
            style={{ backgroundImage: `url(${image})` }}
          />
        ))}
        <div className="absolute inset-0 bg-black bg-opacity-50" />
      </div>

      {/* Main Content */}
      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center px-4">
        
        {/* Heart Animation */}
        <div className="mb-8 relative">
          <div className="heart-container">
            <div className="heart heart-1"></div>
            <div className="heart heart-2"></div>
            <div className="heart heart-3"></div>
          </div>
        </div>

        {/* Slogan */}
        <div className="text-center mb-8">
          <h1 className="text-4xl md:text-6xl font-bold text-white mb-4 animate-fadeInUp">
            Nothing
          </h1>
          <p className="text-xl md:text-2xl text-white font-light animate-fadeInUp animation-delay-300">
            "Crafting Dreams into Reality, One Stitch at a Time"
          </p>
        </div>

        {/* Coming Soon Section */}
        <div className="text-center mb-12 animate-fadeInUp animation-delay-600">
          <h2 className="text-5xl md:text-7xl font-bold text-white mb-6">
            Coming Soon
          </h2>
          <p className="text-lg md:text-xl text-white opacity-90 max-w-2xl mx-auto">
            Our bed and pillow covers store is launching soon! Experience luxury comfort with our premium embroidered collection.
          </p>
        </div>

        {/* Contact Form */}
        <div className="bg-white bg-opacity-10 backdrop-blur-md rounded-2xl p-8 w-full max-w-md animate-fadeInUp animation-delay-900">
          <h3 className="text-2xl font-bold text-white text-center mb-2">Get in Touch</h3>
          <p className="text-white text-center mb-6 opacity-90">
            Have questions about our bed and pillow covers? We'd love to hear from you!
          </p>
          
          <div className="space-y-4">
            <input 
              name="name" 
              type="text"
              placeholder="Your Name" 
              value={formData.name}
              onChange={handleChange} 
              required 
              className="w-full px-4 py-3 rounded-lg bg-white bg-opacity-20 text-white placeholder-white placeholder-opacity-70 border border-white border-opacity-30 focus:outline-none focus:ring-2 focus:ring-white focus:ring-opacity-50"
            />
            
            <input 
              name="email" 
              type="email" 
              placeholder="Your Email" 
              value={formData.email}
              onChange={handleChange} 
              required 
              className="w-full px-4 py-3 rounded-lg bg-white bg-opacity-20 text-white placeholder-white placeholder-opacity-70 border border-white border-opacity-30 focus:outline-none focus:ring-2 focus:ring-white focus:ring-opacity-50"
            />
            
            <textarea 
              name="message" 
              placeholder="Your Message" 
              value={formData.message}
              onChange={handleChange} 
              rows="4"
              required 
              className="w-full px-4 py-3 rounded-lg bg-white bg-opacity-20 text-white placeholder-white placeholder-opacity-70 border border-white border-opacity-30 focus:outline-none focus:ring-2 focus:ring-white focus:ring-opacity-50 resize-none"
            />
            
            <button 
              onClick={handleSubmit}
              className="w-full bg-gradient-to-r from-pink-500 to-red-500 text-white font-bold py-3 px-6 rounded-lg hover:from-pink-600 hover:to-red-600 transform hover:scale-105 transition-all duration-300 shadow-lg"
            >
              Send Message
            </button>
          </div>
        </div>

        {/* Footer */}
        <div className="mt-8 text-center animate-fadeInUp animation-delay-1200">
          <p className="text-white opacity-70 text-sm">
            © 2025 <Nothing></Nothing> - All Rights Reserved
          </p>
        </div>
      </div>

      <style jsx>{`
        @keyframes heartBeat {
          0%, 100% { transform: scale(1) rotate(-45deg); }
          50% { transform: scale(1.2) rotate(-45deg); }
        }
        
        @keyframes heartFloat {
          0%, 100% { transform: translateY(0) rotate(-45deg); }
          50% { transform: translateY(-20px) rotate(-45deg); }
        }
        
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .heart-container {
          position: relative;
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100px;
        }

        .heart {
          position: absolute;
          width: 60px;
          height: 54px;
          background: linear-gradient(45deg, #ff6b6b, #ff8e8e);
          transform: rotate(-45deg);
          animation: heartBeat 2s ease-in-out infinite;
        }

        .heart:before,
        .heart:after {
          content: '';
          position: absolute;
          width: 60px;
          height: 60px;
          background: linear-gradient(45deg, #ff6b6b, #ff8e8e);
          border-radius: 50%;
        }

        .heart:before {
          top: -30px;
          left: 0;
        }

        .heart:after {
          top: 0;
          left: 30px;
        }

        .heart-1 {
          animation-delay: 0s;
          opacity: 0.8;
        }

        .heart-2 {
          animation: heartFloat 3s ease-in-out infinite;
          animation-delay: 0.5s;
          opacity: 0.6;
          transform: scale(0.7) rotate(-45deg);
          left: -40px;
        }

        .heart-3 {
          animation: heartFloat 3s ease-in-out infinite;
          animation-delay: 1s;
          opacity: 0.6;
          transform: scale(0.7) rotate(-45deg);
          right: -40px;
        }

        .animate-fadeInUp {
          animation: fadeInUp 1s ease-out forwards;
        }

        .animation-delay-300 {
          animation-delay: 0.3s;
          opacity: 0;
        }

        .animation-delay-600 {
          animation-delay: 0.6s;
          opacity: 0;
        }

        .animation-delay-900 {
          animation-delay: 0.9s;
          opacity: 0;
        }

        .animation-delay-1200 {
          animation-delay: 1.2s;
          opacity: 0;
        }

        /* Glassmorphism effect for form */
        .backdrop-blur-md {
          backdrop-filter: blur(12px);
          -webkit-backdrop-filter: blur(12px);
        }
      `}</style>
    </div>
  );
};

export default Contact;
